mapper.select:
select goodsNm
from product
where goodsNo = #{goodsNo}

select goodsPrice
from product
where goodsNo = #{goodsNo}

select orderCnt
from product
where goodsNo = #{goodsNo}

select *
from product
where goodsNo = #{product} 
and totalStock >= #{quantity}

select totalStock
from product
where goodsNo = #{goodsNo}

mapper.update:
update product
set totalStock = #{totalStock}
where goodsNo = #{goodsNo}

update orders
set orderStat = '3'
where orderId = #{orderId}

update orders
set orderId = #{orderId}
where orderId = #{beforOrderId}

update orders
set orderProduct = #{orderProduct}
where orderId = #{orderId}

